import java.util.Scanner;
public class Evenodd {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i;
		Scanner sd=new Scanner(System.in);
		i=sd.nextInt();
         if( i%2==0){
        	 System.out.println("even");
         }
         else
         {
        	 System.out.println("odd");
         }
        	 
	}

}
